import { Component } from '@angular/core';

@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {
  username: string ='';
  password: string = '';

  register() {
    // Perform registration logic here, e.g., make an HTTP request to a backend API
    const userData: any[] = JSON.parse(localStorage.getItem('userData') ?? '[]');
    const user = {
      username: this.username,
      password: this.password
    }
    userData.push(user)
    localStorage.setItem('userData', JSON.stringify(userData));
    alret('Sucessfully registered');
  }
}
<h2>Registration</h2>

<div>
  <label for="username">Username:</label>
  <input type="text" id="username" [(ngModel)]="username" required>
</div>

<div>
  <label for="password">Password:</label>
  <input type="password" id="password" [(ngModel)]="password" required>
</div>

<button (click)="register()">Register</button>
